import React, { Component } from "react";
import {
  initScreen,
  setAs400Screen,
  setAs400Style,
  setAS400Cursor
} from "./controler";

import "./css/style.css";

export const EmulatorView = props => {
  const { id, readOnly } = props;
  return (
    <div className="as400-container">
      <div className="screen">
        <div className={`${id} as400-highlight`}></div>
        <div className={`${id} as400-style`}></div>
        <textarea
          readOnly={readOnly ? readOnly : false}
          className={`${id} as400-screen`}
        ></textarea>
        <textarea
          readOnly={readOnly ? readOnly : false}
          className={`${id} as400-screen-temp`}
        ></textarea>
      </div>
    </div>
  );
};

class Emulator extends Component {
  constructor(props) {
    super(props);
    const { id, rows, cols, text, style, cursor } = props;

    this.state = props;
  }

  componentDidMount() {
    const { id, rows, cols, text, style, cursor } = this.state;

    console.log("state: ", this.state);

    initScreen(id, rows, cols);
    setAs400Screen(id, text);
    setAs400Style(id, text, style);
    setAS400Cursor(id, cursor);
  }

  render() {
    const { id, data } = this.props;
    return <EmulatorView id={id} data={data} />;
  }
}
export default Emulator;
