import React, { Component } from "react";
import $ from "jquery";

import "../../css/style.css";

// import * as AS400Utils from "./AS400Utils";

class Emulator extends Component {
  constructor(props) {
    super(props);

    const { id } = props;
    this.state = {
      id: id
    };
  }

  componentDidMount() {
    const data = this.props.data;

    const { rows, cols } = data.screen;
    const text = data.Text;
    const style = data.Style;
    const cursor = data.Cursor;

    initScreen(this.props.id, rows, cols);
    setAs400Screen(this.props.id, text);
    setAs400Style(this.props.id, text, style);
    setAS400Cursor(this.props.id, cursor);
  }

  render() {
    const { id } = this.props;

    return (
      <div className="as400-container">
        {/* <div className="as400-as400-area"> */}
        <div className="screen">
          <div className={`${id} as400-highlight`}></div>
          <div className={`${id} as400-style`}></div>
          <textarea className={`${id} as400-screen`}></textarea>
          <textarea className={`${id} as400-screen-temp`}></textarea>
        </div>
        {/* </div> */}
      </div>
    );
  }
}
export default Emulator;
