import React, { Component } from "react";
import $ from "jquery";

import "../../css/style.css";

class Emulator extends Component {
  constructor(props) {
    super(props);

    const { id } = props;
    this.state = {
      id: id
    };
  }

  componentDidMount() {
    const data = this.props.data;

    const { rows, cols } = data.screen;
    const text = data.Text;
    const style = data.Style;
    const cursor = data.Cursor;

    this.initScreen(this.props.id, rows, cols);
    this.setAs400Screen(this.props.id, text);
    this.setAs400Style(this.props.id, text, style);
    this.setAS400Cursor(this.props.id, cursor);
  }

  getHighlightDefault = (rowSize, colSize) => {
    let textHighlight = "";
    for (let i = 0; i < rowSize; i += 1) {
      for (let j = 0; j < colSize; j += 1) {
        textHighlight += ".";
      }
      textHighlight += "\n";
    }
    return textHighlight;
  };

  getIndexByRowCol = (row, colSize) => (row - 1) * 80 + colSize + row - 2;

  setSelectionRange = (elStr, selectionStart, selectionEnd) => {
    const elScreen = $(`.${elStr}.as400-screen`)[0];

    let start = selectionStart;
    let end = selectionEnd;

    start -= 1;
    end -= 1;

    // elScreen.focus();
    if (elScreen.setSelectionRange) {
      elScreen.setSelectionRange(start, end);
    } else if (elScreen.createTextRange) {
      const range = elScreen.createTextRange();
      range.collapse(true);
      range.moveEnd("character", end);
      range.moveStart("character", start);
      range.select();
    }
  };

  initScreen = (elStr, rowSize, colSize) => {
    const elScreen = $(`.${elStr}.as400-screen`);
    elScreen.attr("rows", rowSize);
    elScreen.attr("cols", colSize);

    const elScreenTemp = $(`.${elStr}.as400-screen-temp`);
    elScreenTemp.attr("rows", rowSize);
    elScreenTemp.attr("cols", colSize);

    const elStyle = $(`.${elStr}.as400-style`);
    elStyle.attr("rows", rowSize);
    elStyle.attr("cols", colSize);

    const elHighlight = $(`.${elStr}.as400-highlight`);
    elHighlight.html(this.getHighlightDefault(rowSize, colSize));
  };

  setAs400Screen = (elStr, data) => {
    const elScreen = $(`.${elStr}.as400-screen`);
    elScreen.val(data);
  };

  setAs400Style = (elStr, text, styleParams) => {
    const elStyle = $(`.${elStr}.as400-style`);

    let style = styleParams;
    //   style = `..................................RRRRRRRRRRRRR.................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // .........................................................UUUUUUUUUU.............\n
    // .........................................................HHHHHHHHHH.............\n
    // .........................................................UUUUUUUUUU.............\n
    // .........................................................UUUUUUUUUU.............\n
    // .........................................................UUUUUUUUUU.............\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................\n
    // ................................................................................`;

    console.log("styleParams: ", styleParams);

    style = style.split("\n").join("");

    let textStyle = "";
    for (let i = 0; i < text.length; i += 1) {
      const character = text[i];
      const styleCharacter = style[i];

      if (styleCharacter !== "." && styleCharacter !== "↵") {
        console.log("styleCharacter: ", styleCharacter);

        textStyle += character.replace(
          character,
          `<as400-${styleCharacter.toLowerCase()}-style>$&</as400-${styleCharacter.toLowerCase()}-style>`
        );
      } else {
        textStyle += character;
      }
    }

    $(elStyle).html(textStyle);
  };

  clearHighlight = (elStr, markType) => {
    const elHighlight = $(`.${elStr}.as400-highlight`);
    let newHighlight = elHighlight.html();

    newHighlight = newHighlight
      .replace(`<as400-${markType}>`, "")
      .replace(`</as400-${markType}>`, "");

    elHighlight.html(newHighlight);
  };

  getAS400Cursor = posStr => {
    const posArr = posStr.split(",");

    return {
      row: parseInt(posArr[0]),
      col: parseInt(posArr[1])
    };
  };

  setAS400Cursor = (elStr, data) => {
    const pos = this.getAS400Cursor(data);

    const index = this.getIndexByRowCol(pos.row, pos.col);

    this.setSelectionRange(elStr, index, index);
  };

  convertRowColNumber = posStr => {
    const posArr = posStr.trim().split(",");
    return {
      row: posArr[0],
      col: posArr[1]
    };
  };

  getRowNumberByIndex = (elStr, index) => {
    const elScreen = $(`.${elStr}.as400-screen`);
    return (
      elScreen
        .val()
        .substr(0, index)
        .split("\n").length - 1
    );
  };

  getColNumberByIndex = (elStr, index, colSize) => {
    const row = this.getRowNumberByIndex(elStr, index);
    const col = (index - row) % colSize;

    if (index === colSize * (row + 1) + row) {
      return colSize;
    }
    return col;
  };

  setIndexByRowCol = (elStr, row, col) => {
    const index = this.getIndexByRowCol(row, col);
    this.setSelectionRange(elStr, index, index);
  };

  getStartSelection = (elStr, colSize) => {
    const elScreen = $(`.${elStr}.as400-screen`);
    const startIndex = elScreen.prop("selectionStart");

    const rowSelectionStartIndex =
      this.getRowNumberByIndex(elStr, startIndex) + 1;
    const colSelectionStartIndex =
      this.getColNumberByIndex(elStr, startIndex, colSize) + 1;

    return {
      row: rowSelectionStartIndex,
      col: colSelectionStartIndex
    };
  };

  getEndSelection = (elStr, colSize) => {
    const elScreen = $(`.${elStr}.as400-screen`);
    const endIndex = elScreen.prop("selectionEnd");
    const rowSelectionEndIndex = this.getRowNumberByIndex(elStr, endIndex) + 1;
    const colSelectionEndIndex =
      this.getColNumberByIndex(elStr, endIndex, colSize) + 1;

    return {
      row: rowSelectionEndIndex,
      col: colSelectionEndIndex
    };
  };

  getSelectionRange = (elStr, selectionStart, selectionEnd) => {
    const elScreen = $(`.${elStr}.as400-screen`);

    if (selectionStart !== selectionEnd) {
      // selectionStart -= 1;

      const selection = elScreen.val().substring(selectionStart, selectionEnd);
      return selection;
    }
    return "";
  };

  render() {
    const { id } = this.props;

    return (
      <div className="as400-container">
        {/* <div className="as400-as400-area"> */}
        <div className="screen">
          <div className={`${id} as400-highlight`}></div>
          <div className={`${id} as400-style`}></div>
          <textarea className={`${id} as400-screen`}></textarea>
          <textarea className={`${id} as400-screen-temp`}></textarea>
        </div>
        {/* </div> */}
      </div>
    );
  }
}
export default Emulator;
