import React from "react";
import Emulator from "../Emulator/index";
import EmulatorView from "../Emulator/EmulatorView";

import {
  initScreen,
  setAs400Screen,
  setAs400Style,
  setAS400Cursor
} from "../Emulator/controler";

export default class ScreenMapping extends Emulator {
  constructor(props) {
    super(props);

    this.state = {};
  }

  componentDidMount() {
    const { id, rows, cols, text, style, cursor } = this.state;

    initScreen(id, rows, cols);
    setAs400Screen(id, text);
    setAs400Style(id, text, style);
    setAS400Cursor(id, cursor);
  }

  render() {
    console.log("props: ", this.props);

    const { id, readOnly } = this.props;
    return <EmulatorView id={id} readOnly={readOnly} />;
  }
}
