import React from "react";
import PropTypes from "prop-types";

import $ from "jquery";
import _ from "lodash";

import Emulator from "../Emulator/index";
import EmulatorView from "../Emulator/EmulatorView";

import {
  getIndexByRowCol,
  getHighlightDefault,
  getSelectionRange,
  getStartSelection
} from "../Emulator/controler";

export class ScreenMapping extends Emulator {
  constructor(props) {
    super(props);

    this.state = {
      id: props.id,
      markList: props.highlightList,
      lastSelection: {
        startIndex: 0,
        endIndex: 0
      }
    };
  }

  componentDidMount(...args) {
    super.componentDidMount.apply(this, args);
    this.markAllCheckString(this.props.highlight);
  }

  markAllCheckString = markListData => {
    this.clearAllMarkChackString(this.state.id);
    this.setState({
      markList: markListData
    });

    const elHighlight = $(`.${this.state.id}.as400-highlight`);
    const text = elHighlight[0].innerHTML;
    let textHighlight = "";
    for (let i = 0; i < text.length; i += 1) {
      if (
        !_.isEmpty(markListData.mark1) &&
        i === getIndexByRowCol(markListData.mark1.row, markListData.mark1.col)
      ) {
        if (markListData.mark1.text.length < 2) {
          textHighlight += `<as400-mark-1>${text[i]}</as400-mark-1>`;
        } else {
          textHighlight += `<as400-mark-1>${text[i]}`;
        }
      } else if (
        !_.isEmpty(markListData.mark1) &&
        i ===
          getIndexByRowCol(markListData.mark1.row, markListData.mark1.col) +
            markListData.mark1.text.length -
            1
      ) {
        textHighlight += `${text[i]}</as400-mark-1>`;
      } else if (
        !_.isEmpty(markListData.mark2) &&
        i === getIndexByRowCol(markListData.mark2.row, markListData.mark2.col)
      ) {
        if (markListData.mark2.text.length < 2) {
          textHighlight += `<as400-mark-2>${text[i]}</as400-mark-2>`;
        } else {
          textHighlight += `<as400-mark-2>${text[i]}`;
        }
      } else if (
        !_.isEmpty(markListData.mark2) &&
        i ===
          getIndexByRowCol(markListData.mark2.row, markListData.mark2.col) +
            markListData.mark2.text.length -
            1
      ) {
        textHighlight += `${text[i]}</as400-mark-2>`;
      } else if (
        !_.isEmpty(markListData.mark3) &&
        i === getIndexByRowCol(markListData.mark3.row, markListData.mark3.col)
      ) {
        if (markListData.mark3.text.length < 2) {
          textHighlight += `<as400-mark-3>${text[i]}</as400-mark-3>`;
        } else {
          textHighlight += `<as400-mark-3>${text[i]}`;
        }
      } else if (
        !_.isEmpty(markListData.mark3) &&
        i ===
          getIndexByRowCol(markListData.mark3.row, markListData.mark3.col) +
            markListData.mark3.text.length -
            1
      ) {
        textHighlight += `${text[i]}</as400-mark-3>`;
      } else {
        textHighlight += text[i];
      }
    }

    elHighlight.html(textHighlight);
  };

  clearAllMarkChackString = elStr => {
    const elHighlight = $(`.${elStr}.as400-highlight`);
    elHighlight.html(getHighlightDefault(24, 80));

    this.setState({
      markList: {}
    });
  };

  markStringChackString = (markType, highlight) => {
    let newMarkList = "";
    const markList = this.state.markList;

    newMarkList = {
      ...markList,
      [markType.replace("-", "")]: highlight
    };

    this.setState({ markList: newMarkList });
    this.markAllCheckString(newMarkList);
  };

  clearMarkString = markType => {
    let newMarkList = "";
    const markList = this.state.markList;
    const markData = {};

    newMarkList = {
      ...markList,
      [markType.replace("-", "")]: markData
    };

    this.setState({ markList: newMarkList });
    this.markAllCheckString(newMarkList);
  };

  onSelect = (id, colSize) => {
    const elScreen = $(`.${id}.as400-screen`);

    elScreen.select(event => {
      // console.log(`| ========== ${event.type} ========== |`);
      const start = event.target.selectionStart;
      const end = event.target.selectionEnd;
      const startPosition = getStartSelection(id, colSize);

      setLastSelection({
        text: getSelectionRange(id, start, end),
        row: startPosition.row,
        col: startPosition.col
      });
    });
  };

  /**
   * ================
   * provide function
   * ================
   */
  getHighlight = markType => {
    return this.state.markList[markType.replace("-", "")];
  };

  setHighlight = (markType, highlight) => {
    this.markStringChackString(markType, highlight);
  };

  clearHighlight = markType => {
    return this.clearMarkString(markType);
  };

  getHighlightList = () => {
    return this.state.markList;
  };

  setHighlightList = highlightList => {
    this.markAllCheckString(highlightList);
  };

  clearHighlightList = () => {
    this.clearAllMarkChackString(this.props.id);
  };

  render() {
    const { id, readOnly } = this.props;
    return <EmulatorView id={id} readOnly={readOnly} />;
  }

  // Set default props
  static defaultProps = {
    highlight: {}
  };
}

ScreenMapping.propTypes = {
  highlightList: PropTypes.object
};

export default ScreenMapping;
