import React from "react";

export { default as Emulator } from "./Emulator/index";
export { default as EmulatorReadOnly } from "./EmulatorReadOnly/index";

const Emulator = props => {
  const { id, readOnly } = props;

  return (
    <div className="as400-container">
      <div className="as400-as400-area">
        <div className="screen">
          <div className={`${id} as400-highlight`}></div>
          <div className={`${id} as400-style`}></div>
          <textarea
            className={`${id} as400-screen`}
            readOnly={readOnly || false}
          ></textarea>
          <textarea
            className={`${id} as400-screen-temp`}
            readOnly={readOnly || false}
          ></textarea>
        </div>
      </div>
    </div>
  );
};

export default Emulator;
