import React from "react";

import Emulator from "../Emulator";

import "../../css/style.css";

class EmulatorView extends Emulator {
  constructor(props) {
    super(props);

    const { id, children } = props;

    this.state = {
      id: id
    };
  }

  componentDidMount() {
    const data = this.props.data;

    const { rows, cols } = data.screen;
    const text = data.Text;
    const style = data.Style;
    const cursor = data.Cursor;

    this.initScreen(this.props.id, rows, cols);
    this.setAs400Screen(this.props.id, text);
    this.setAs400Style(this.props.id, text, style);
    this.setAS400Cursor(this.props.id, cursor);
  }

  render() {
    const { id } = this.props;

    console.log("Emulator: ", Emulator);

    return (
      <div className="as400-container">
        {/* <div className="as400-as400-area"> */}
        <div className="screen">
          <div className={`${id} as400-highlight`}></div>
          <div className={`${id} as400-style`}></div>
          <textarea className={`${id} as400-screen`} readOnly></textarea>
          <textarea className={`${id} as400-screen-temp`} readOnly></textarea>
        </div>
        {/* </div> */}
      </div>
    );
  }
}
export default EmulatorView;
